import React, { Component } from "react";
import { NavLink } from "react-router-dom";

export default class Transformer extends Component {
    constructor(props) {
        super(props);
        this.state = {
        value: 'Please paste the lab data.',
        value_transform: '',
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({value: event.target.value});
    }

    handleSubmit(event) {
        const Hb = this.state.value;
        const Hb_find = Hb.search("HB");
        const Plt = this.state.value;
        const Plt_find = Plt.search("PLT");
        this.setState({value_transform: this.state.value_transform.concat((this.state.value).slice(Hb_find,Hb_find+13)).concat('<br />').concat((this.state.value).slice(Plt_find,Plt_find+13))});
        event.preventDefault();
    }

    render() {
        return (
        <form onSubmit={this.handleSubmit}>
            <br></br>
            <label>
            Lab<br></br>
            <textarea value={this.state.value} rows={8} cols={50} onChange={this.handleChange} />
            </label>
            <br></br>
            <input type="submit" value="Transform" />
            <br></br>
            <label>
            <br></br>
            Lab selected<br></br>
            <textarea value={this.state.value_transform} rows={8} cols={50}/>
            </label>
        </form>
        );
    }
}
