import React, { Component } from "react";
import { NavLink } from "react-router-dom";

export default class MELD extends Component {
        constructor(props) {
          super(props);
          this.state = { 

            TIMI_PosCard: 0,
            Ascites: "none",
            TBIL: "none",
            CPscore: 0 
         };
        }
        changeAscites = (e) => this.setState({Ascites: e.target.value});
        changeTBIL = (e) => this.setState({TBIL: e.target.value});
        changeAscites = (e) => this.setState({Ascites: e.target.value});
        changeAscites = (e) => this.setState({Ascites: e.target.value});
        changeAscites = (e) => this.setState({Ascites: e.target.value});
        changeAscites = (e) => this.setState({Ascites: e.target.value});


        handleCPscore = () => {
            if(this.state.Ascites=="none"){
                this.setState({CPscore_a: 1})
            }
            else if(this.state.Ascites=="easily controlled"){
                this.setState({CPscore_a: 2})
            }else{
                this.setState({CPscore_a: 3})
            }
            };

    render() {
        return (
            <div>
                <h2><font color="#1e6b33">Chigh-Pugh Score</font></h2>
                <a>Asites </a>
                <input type="radio" value="none" checked={this.state.Ascites==="none"} onChange={this.changeAscites} />none<a>&nbsp;</a>
                <input type="radio" value="easily controlled" checked={this.state.Ascites==="easily controlled"} onChange={this.changeAscites}/>easily controlled<a>&nbsp;</a>
                <input type="radio" value="refractory" checked={this.state.Ascites==="refractory"} onChange={this.changeAscites}/>refractory<a>&nbsp;</a>
                <br></br>
                <a>T-bil </a>
                <input type="radio" value="none" checked={this.state.Ascites==="none"} onChange={this.changeTBIL} />2mg/dl<a>&nbsp;</a>
                <input type="radio" value="easily controlled" checked={this.state.Ascites==="easily controlled"} onChange={this.changeTBIL}/>2-3mg/dl<a>&nbsp;</a>
                <input type="radio" value="refractory" checked={this.state.Ascites==="refractory"} onChange={this.changeTBIL}/>refractory<a>&nbsp;</a>
                <br></br>
                <a>Albumin </a>
                <input type="radio" value="none" checked={this.state.Ascites==="none"} onChange={this.changeAscites} />3.5mg/dl<a>&nbsp;</a>
                <input type="radio" value="easily controlled" checked={this.state.Ascites==="easily controlled"} onChange={this.changeAscites}/>2.8-3.5mg/dl<a>&nbsp;</a>
                <input type="radio" value="refractory" checked={this.state.Ascites==="refractory"} onChange={this.changeAscites}/>2.8mg/dl<a>&nbsp;</a>
                <br></br>
                <a>PT INR </a>
                <input type="radio" value="none" checked={this.state.Ascites==="none"} onChange={this.changeAscites} />1.7<a>&nbsp;</a>
                <input type="radio" value="easily controlled" checked={this.state.Ascites==="easily controlled"} onChange={this.changeAscites}/>1.7-2.2<a>&nbsp;</a>
                <input type="radio" value="refractory" checked={this.state.Ascites==="refractory"} onChange={this.changeAscites}/>2.2<a>&nbsp;</a>
                <br></br>
                <a>Hepatic encephalopathy </a>
                <input type="radio" value="none" checked={this.state.Ascites==="none"} onChange={this.changeAscites} />nil<a>&nbsp;</a>
                <input type="radio" value="easily controlled" checked={this.state.Ascites==="easily controlled"} onChange={this.changeAscites}/>Grade 1-2<a>&nbsp;</a>
                <input type="radio" value="refractory" checked={this.state.Ascites==="refractory"} onChange={this.changeAscites}/>Grade 3-4<a>&nbsp;</a>
                <br></br>
                <h3>Score: {this.state.CPscore}</h3>
                <span>
                    <a>&nbsp;&nbsp;</a>
                    <button onClick={this.handleCPscore}><a>&nbsp;</a>calculate</button>
                </span>
            </div>
        );
    }
}
